INSERT INTO `productos` (`id_prod`, `nombre`, `detalle`, `img`, `titulo`, `resumen`, `practicas_realizables`, `url_pdf`) VALUES
(1, 'Banco de Montaje para Modelos Mecánicos', 'Aplicable para todos los aparatos de ensayo de los siguientes modelos \r\n\r\nEl banco de montaje sirve para la colocación cómoda de los siguientes modelos. A través de varios agujeros pueden atornillarse todos los aparatos correspondientes. Lateralmente al banco de montaje hay montado una pizarra regulable en altura. El bastidor es móvil, las ruedas pueden bloquearse para lograr una estabilidad segura.', '', 'Movimiento Rectilinio Uniforme', '', '', ''),
(9, 'Métodos de Medición y Características de los Cuerpos', '\r\n\r\n', '../Img/img9.JPG', '', '', '•Medición de longitudes, ancho, altura, profundidad, espesor; y medición de diámetros internos y externos\r\n•Medición de superficies regulares e irregulares\r\n•Medición de volúmenes de sólidos, líquidos y gases\r\n•Medición de volumen de figuras regulares e irregulares, por el método de rebalse\r\n•Medición del volumen neumático de gases\r\n•Medición del tiempo con péndulo y cronómetro\r\n•Medición de la masa con balanzas\r\n•Medición de la densidad de cuerpos regulares en base a su masa y al volumen calculado\r\n•Medición de la densidad de cuerpos irregulares en base a la masa y al volumen desplazado\r\n•Medición de la densidad de diferentes líquidos\r\n•Masa, peso y fuerza\r\n', ''),
(19, 'Modelo Experimental de Movimiento Lineal', '\r\nModelo Experimental de Movimiento Lineal<br />\r\n\r\n- Ensayo sobre la teoría del movimiento en un plano oblicuo <br />\r\n- Impacto elástico <br />\r\n- Impacto plástico <br />\r\n- Impulso<br />\r\n- Determinación del ángulo e índice de fricción<br />\r\n\r\n', '../Img/img19.jpg', '', 'Modelo Experimental de Movimiento Lineal', 'Medición y Experimentación del Movimiento Lineal', ''),
(4, 'Segunda Ley de Newton', '', '', 'Segunda Ley de Newton', 'rewsumen', '', '../pdfs/serial_nero7.txt'),
(5, 'CANAL PARA LA ENSEÑANZA DE USO MÚLTIPLE', 'Canal con estructura de perfil de acero y paredes de vidrio. Tiene un tanque grande como depósito de entrada con dos disipadores de energía en forma de rejilla a fin de reducir la turbulencia de flujo de agua, con una llave de desagüe en el fondo del depósito. Otro depósito se encuentra al final del canal con un tubo de plástico en el fondo que sirve como desagüe a un tanque de fibra de vidrio. Conectado a través de una manguera a una bomba. \r\n<br>\r\nLa bomba y su interruptor están sujetos en una estructura de tubo de acero que sirve como soporte a un extremo del canal, con un eje que sirve como pivote del canal. El otro extremo lleva otro apoyo con un tornillo para inclinar el canal.\r\n', '../Img/img5.jpg', '', 'Canal con estructura de perfil de acero y paredes de vidrio. Tiene un tanque grande como depósito de entrada con dos disipadores de energía en forma de rejilla a fin de reducir la turbulencia de flujo de agua, con una llave de desagüe en el fondo del depósito.', '• Medición del caudal a través de vertedero rectangular de pared delgada y contracción lateral.<br>\r\n• Medición del caudal a través de compuerta con descarga en el fondo.<br>\r\n• Fuerzas en una compuerta de fondo<br>\r\n• Tirante crítico- derivación de la ecuación de la energía específica<br>\r\n• Flujo Uniforme<br>\r\n• Resalto hidráulico como disipador de energía.<br>\r\n• Flujo sobre un vertedero triangular de pared espesa, curvas de Remanso.<br>\r\n• Estudio de vertederos de pared delgada sin contracción lateral.<br>', '../pdfs/pdf5.pdf'),
(45, 'Líneas de campo eléctrico y líneas equipotenciales', 'Equipo para la medición de Líneas de campo eléctrico y líneas equipotenciales', '../Img/img45.jpg', '', 'Equipo para la medición de Líneas de campo eléctrico y líneas equipotenciales', '- Líneas equipotenciales en campos eléctricos', ''),
(7, 'BANCO BÁSICO PARA HIDRODINÁMICA', 'Descripción:<br>\r\nUnidad de servicio completa y móvil, hecha en tubo de acero con cuatro ruedas. Depósito de paredes de vidrio. Tiene un depósito como cubeta de trabajo para medir volúmenes de agua depositadas para un tiempo determinado. Lleva una bomba centrífuga, dos válvulas para regular el caudal de entrada a cada equipo que trabaje con el banco básico, y un medidor de caudal tipo rotámetro.\r\n<br>\r\nNota 1: Los equipos que se describen a continuación trabajan complementariamente con el banco básico para hidrodinámica.', '../Img/img7.jpg', '', 'Unidad de servicio completa y móvil, hecha en tubo de acero con cuatro ruedas.', '', '<NULL>'),
(8, 'EQUIPO DE PERDIDAS EN TUBERÍAS Y ACCESORIOS', 'Sistema de tuberías de distinto material y sección, cuenta con todos los accesorios de instalación para distribuciones. La medida de las pérdidas de presión se hace a través de dos manómetros con dos tubos comunicantes. La medida se realiza a través de tomas de presión de forma toroidal, que se encuentran instaladas en las tuberías y accesorios. Todo montado en un panel de tablero melamínico.', '../Img/img8.gif', '', 'Sistema de tuberías de distinto material y sección, cuenta con todos los accesorios de instalación para distribuciones.', '•Pérdidas primarias en tuberías.<br>\r\n•Curva coeficiente de fricción- número de Reynolds.<br>\r\n•Curva pérdida de carga – velocidad.<br>\r\n•Pérdidas secundarias en accesorios.<br>\r\n•Curva pérdida de carga – velocidad (para un accesorio cualquiera).<br>\r\n•Determinación del coeficiente de carga secundaria en accesorios y comparación con lo obtenido en tablas hidráulicas.<br>\r\n•Determinación del coeficiente de fricción en tramos de redes de tuberías.<br>\r\n•Tuberías en serie y en paralelo.<br>\r\n•Perdida total de carga en redes de tuberías.<br>\r\n<br>\r\nEspecificaciones Técnicas:\r\n<br>\r\nNota.- Este equipo funciona con un banco básico para hidrodinámica o también con el modelo de bombas en serie y en paralelo.\r\n<br>', ''),
(10, 'Fuerzas', '', '', '', '', 'Efectos de las fuerzas:\r\n•	Ley de Hooke en resortes\r\n•	Magnitud y dirección de las fuerzas actuantes en una lámina\r\n•	Cambio de dirección de la fuerza por una polea\r\n•	Inclusión de fuerzas en paralelo\r\n•	Adición y descomposición de las fuerzas\r\n\r\nMecánica de cuerpos sólidos:\r\n•Rozamiento (estático, de deslizamiento, de rodadura).\r\n•Medición del coeficiente de fricción de deslizamiento\r\n•Centro de gravedad y equilibrio\r\n\r\nPéndulos:\r\n•Influencia de la longitud y masa de un péndulo en el periodo de oscilación\r\n•Péndulo oscilante, influencia de la constante y masa del resorte en el periodo de oscilación\r\n•Péndulo de torsión, influencia de la masa y el espacio entre masas en el periodo de oscilación.\r\n•Péndulo de muelle\r\n', ''),
(14, 'Medición del volumen y la Densidad', 'Medición del volumen y la Densidad<br>\r\n- Determinación del volumen y densidad de los sólidos<br>\r\n- Determinación de la densidad del aire<br>\r\n', '../Img/img14.gif', '', 'Determinación del volumen y densidad de los sólidos<br>\r\nDeterminación de la densidad del aire<br>\r\n', 'Medición y determinación de volumenes y densidades', ''),
(15, 'Aparato para Experimentos de Estática', '\r\n<html><head></head><body>Este aparato de ensayo permite ilustrar los principios de la estática, tales como equilibrio, descomposición de fuerzas, ley de palanca, etc. El elemento básico consiste en una placa de montaje vertical. Los perfiles que rodean la placa permiten fijar rápidamente las piezas que se necesitan para el ensayo. La red de líneas impresas con una distancia de 50 mm. permite la construcción exacta de estas piezas. Ya no hace falta medir los ángulos, ya que las coordenadas pueden deducirse de las líneas. Dos rodamientos de bolas permiten realizar ensayos de par de fuerzas con poco rozamiento.<br /><br />Para una mejor ilustración, los dinamómetros disponen de un diámetro extraordinariamente grande. Es posible rotular la placa con marcadores.</body></html>', '../Img/img15.jpg', '', 'Este aparato de ensayo permite ilustrar los principios de la estática, tales como equilibrio, descomposición de fuerzas, ley de palanca, etc. ', '- Composición y descomposición de fuerzas mediante paralelogramo de fuerzas.<br>\r\n- Equilibrio de fuerzas.<br> \r\n- Ley de palanca, momentos y equilibrio de momentos. <br>\r\n- Fuerzas en apoyos. <br>\r\n- Comportamiento de dilatación de un resorte helicoidal, ley de Hooke. <br> \r\n- Fricción de deslizamiento en función a la fuerza de aplicación, superficie de aplicación y características materiales del cuerpo de prueba, comparación con la fricción de rodillos.<br>\r\n- Determinación de los coeficientes de fricción.<br>\r\n- Investigación del plano inclinado.<br>\r\n- Reducción de fuerza con polipasto de 4 cordeles. <br>\r\n- Reducción de fuerza con polipasto de 6 cordeles.<br> \r\n- Dependencias y reducción de la fuerza con polipasto diferencial.<br>\r\n- Proporción de transmisión de la velocidad.<br>', ''),
(13, 'Medición de la longitud', 'Medición de la longitud\r\n- Uso del calibre Vernier <br>\r\n- Uso del tornillo micrométrico<br>', '../Img/img13.gif', '', 'Medición de la longitud', 'Medición de la longitud', ''),
(16, 'Equilibrio de Fuerzas Coplanares', '\r\n<html><head></head><body>Equilibrio de Fuerzas Coplanares - Equilibrio en el sistema de fuerzas plano <br />- Desglose de fuerzas <br />- Adición vectorial<br /></body></html>', '../Img/img16.jpg', '', 'Equilibrio de Fuerzas Coplanares', 'Medición en Equilibrio de fuerzas coplanares', ''),
(17, 'Equilibrio de Momentos de Torsión', '\r\n<html><head></head><body>Equilibrio de Momentos de Torsión<br />- Equilibrio de momentos en sistemas de fuerzas planos y de espacio<br />- Adición vectorial<br /></body></html>', '../Img/img17.jpg', '', 'Equilibrio de Momentos de Torsión<br>\r\n', 'Medición para Momentos de Torsión', ''),
(18, 'Rozamiento en Plano Inclinado', '\r\n<html><head></head><body>Rozamiento en Plano Inclinado - Coeficientes de fricción de diferentes pares de material <br />- Paso adherir – deslizar<br />- Representación del &quot;cono de fricción&quot; <br />- Equilibrio estático de fuerzas en el plano inclinado<br /></body></html>', '../Img/img18.jpg', '', 'Rozamiento en Plano Inclinado', 'Experimento para medición y determinación del rozamiento en planos inclinados', ''),
(20, 'Movimiento en una dimensión en el carril con colchón de aire', 'Movimiento en una dimensión en el carril con colchón de aire<br>\r\n- Cinemática del movimiento lineal<br>\r\n- Segunda ley de Newton<br>\r\n- Conservación del momento lineal<br>\r\n- Conservación de la energía<br>', '../Img/img20.gif', '', 'Movimiento en una dimensión en el carril con colchón de aire', 'Distintas prácticas de movimiento en una dimensión.', ''),
(21, 'Caída libre', 'Caída libre<br>\r\n\r\n- Estudio del movimiento de caída libre\r\n', '../Img/img21.gif', '', 'Caída Libre', 'Equipo para la práctica de Caída Libre', ''),
(22, 'Dispositivo de lanzamiento vertical y horizontal', 'Dispositivo de lanzamiento vertical y horizontal<br>\r\n- Independencia de la aceleración vertical de caída con respecto a los elementos de velocidad horizontal al lanzamiento', '../Img/img22.gif', '', 'Dispositivo de lanzamiento vertical y horizontal<br>\r\n', 'Pruebas de lanzamiento vertical', ''),
(23, 'Movimiento de proyectiles', 'Movimiento de proyectiles<br>\r\n- Estudio del movimiento parabólico<br>\r\n- Principio de superposición del movimiento vertical y horizontal\r\n', '../Img/img23.gif', '', 'Movimiento de proyectiles<br>\r\n', 'Prácticas de movimiento de proyectiles ', ''),
(24, 'Momento de Inercia Rotacional', 'Equipo para la práctica y experimentación del Momento de Inercia Rotacional', '../Img/img24.jpg', '', 'Equipo para la práctica y experimentación del Momento de Inercia Rotacional', '- Trabajar el concepto "Momento de inercia" <br>\r\n- Determinación de los momentos de inercia de diferentes cuerpos <br>\r\n- Efecto de la masa giratoria en el momento de inercia <br>\r\n- Efecto del radio de rotación en el momento de inercia <br>\r\n- Comparación de los momentos de inercia de cilindros macizos y huecos con la misma masa y diámetro exterior<br>', ''),
(25, 'Dinámica de un Cuerpo Rígido sobre el Plano Inclinado', 'Equipo para la experimentación de la Dinámica un cuerpo rígido', '../Img/img25.gif', '', 'Equipo para la experimentación de la Dinámica un cuerpo rígido', '- Justificante de la ley de gravedad en un plano inclinado <br>\r\n- Efecto de la masa de un cuerpo a su aceleración<br>\r\n', ''),
(26, 'Aparato para Medir la Fuerza Centrípeta', '\r\n<html><head></head><body>Aparato para la experimentación y medición de la fuerza centrípeta</body></html>', '../Img/img26.jpg', '', 'Aparato para Medir la Fuerza Centrípeta', 'Dependencia de la fuerza centrípeta<br>\r\n- del régimen de revoluciones<br>\r\n- del tamaño de la masa giratoria<br>\r\n- del radio de rotación<br>\r\n', ''),
(27, 'Deformación Elástica en Alambres', 'Este equipo pone de manifiesto la relación entre esfuerzo de tracción y cambio de longitud elástica de alambres y permite sacar conclusiones sobre los coeficientes del material del alambre. Los alambres se cuelgan de una viga montada en el soporte. Los extremos inferiores se sujetan a dos fijadores. Un travesaño, en el que se cuelga un juego de pesas para cargar los alambres, los une y permite una carga uniforme. El alargamiento de los alambres como consecuencia de la fuerza de tracción puede leerse en los fijadores equipados con calibradores digitales.', '../Img/img27.jpg', '', 'Este equipo pone de manifiesto la relación entre esfuerzo de tracción y cambio de longitud elástica de alambres y permite sacar conclusiones sobre los coeficientes del material del alambre. ', '- Cambio elástico de longitudes de alambres de metal bajo fuerza de tracción.<br>\r\n<li>en dependencia de la carga <br>\r\n<li>en dependencia del diámetro del alambre <br>\r\n- Determinación e inspección del módulo de elasticidad<br>', ''),
(28, 'Deformación de Resortes Helicoidales (Ley de Hooke)', 'Este equipo permite determinar los cambios de longitud de resortes a esfuerzos de diferente altura. Se compone de una placa de base, en la que está montado un soporte para resortes. Un resorte puede alargarse con ayuda de un peso.Los resortes de presión se comprimen con los mismos pesos. Los cambios de longitud se leen con un indicador que discurre a lo largo de una escala milimétrica.', '../Img/img28.jpg', '', 'Este equipo permite determinar los cambios de longitud de resortes a esfuerzos de diferente altura. ', '- Curva característica de resorte de resortes por tracción<br>\r\n- Curva característica de resorte por compresión <br>\r\n', ''),
(29, 'Modelo de un Péndulo Compuesto', 'El modelo sirve para el estudio de las oscilaciones de péndulo. Se compara un péndulo físico con uno matemático. Se incluye para ello una vara metálica con masa adicional corrediza como péndulo rígido. Su punto de suspensión puede regularse en un cojinete corredizo. La longitud del correspondiente hilo de péndulo puede variarse fácilmente con un dispositivo de fijación. Una placa de base se usa para el montaje en el soporte de modelos mecánicos.', '../Img/img29.jpg', '', 'El modelo sirve para el estudio de las oscilaciones de péndulo. Se compara un péndulo físico con uno matemático. ', '- Duración de la oscilación de péndulos simple y péndulo físico <br>\r\n- Definición del punto de gravedad en el péndulo físico <br>\r\n- Reducida longitud de péndulo y centro de inercia del péndulo físico<br>\r\n', ''),
(30, 'Modelo de Vibración Torsional', 'El modelo ofrece la oportunidad de estudiar vibraciones por torsión en barras de torsión. Se compone de un bastidor de base de aluminio montado en el soporte. Su placa portadora dispone de un sólido mandril de sujeción con ayuda del cual pueden fijarse barras de torsión. Un disco y un anillo de la misma masa pueden fijarse a la barra de torsión con un mandril de sujeción. De esta forma se puede ajustar sin más dificultades la longitud efectiva.', '../Img/img30.jpg', '', 'El modelo ofrece la oportunidad de estudiar vibraciones por torsión en barras de torsión. ', '- Determinación de la duración de la oscilación de torsión en función a: <br>\r\n<li>longitud del hilo de torsión <br>\r\n<li>diámetro del hilo de torsión<br>\r\n', ''),
(31, 'Modelo de Vibración Torsional con Resorte Espiral', '\r\n<html><head></head><body>Un muelle espiral de acero se encuentra enclavado entre un eje con un cojinete de rodamiento y un soporte fijo. Sobre el eje asientan dos brazos opuestos entre sí. Dos piezas metálicas corredizas colocadas sobre los brazos pueden deslizarse mediante tornillos de fijación, estas forman las masas rotativas cuyo momento de inercia puede modificarse. El sistema se invierte manualmente. El ángulo de torsión se lee en un disco graduado a precisión de 1°.</body></html>', '../Img/img31.jpg', '', 'Un muelle espiral de acero se encuentra enclavado entre un eje con un cojinete de rodamiento y un soporte fijo. ', '- Determinación de la rigidez de un muelle espiral <br>\r\n- Determinación de la frecuencia propia de un sistema de muelle espiral-masa rotativa <br>\r\n<li>Con modificación de la totalidad de la masa oscilante <br>\r\n<li>Con modificación de la distribución de la masa<br>\r\n', ''),
(32, 'Aparato para Demostración de Oscilaciones', 'Este modelo de mesa sirve para realizar experimentos en sistemas de masa-resorte. Un patín sin  fricción sobre cojinete de rodamiento puede desplazarse verticalmente entre dos columnas de guía pulidas. Puede colocarse una masa adicional y está suspendido de un muelle helicoidal. Su posición cero puede regularse con un husillo roscado en el extremo superior del resorte. Las columnas de guía se encuentran fijas a un sólido pedestal de fundición equipado con asas. Para una amortiguación controlada de las oscilaciones se dispone de un amortiguador hidráulico cargado de aceite.', '../Img/img32.jpg', '', 'Este modelo de mesa sirve para realizar experimentos en sistemas de masa-resorte. ', '- Rigidez de un resorte helicoidal <br>\r\n- Frecuencia y grado de amortiguamiento en un sistema masa-resorte <br>\r\n', ''),
(33, 'Oscilaciones torsionales libres y amortiguadas', 'El juego de ensayo contiene tres barras de torsión distintas y dos discos de masa también diferentes. Un amortiguador de aceite permite atenuar las amplitudes. Los componentes se montan fácilmente y con exactitud en las ranuras de un bastidor.', '../Img/img33.jpg', '', 'El juego de ensayo contiene tres barras de torsión distintas y dos discos de masa también diferentes. ', '- Frecuencia de resonancia de oscilaciones torsionales <br>\r\n- Efecto de la rigidez a la torsión, la masa giratoria y la amortiguación<br>\r\n', ''),
(34, 'Ondas estacionarias en cuerdas', 'Equipo compacto con motor y dinamómetro, sirve para demostración cualitativa y cuantitativa sobre la propagación de ondas transversales a lo largo de cuerdas sometidas a tensión.', '../Img/img34.jpg', '', 'Equipo compacto con motor y dinamómetro, sirve para demostración cualitativa y cuantitativa sobre la propagación de ondas transversales a lo largo de cuerdas sometidas a tensión.', '- Experimento de Melde', ''),
(35, 'Velocidad del sonido', 'Equipo para observar las ondas de sonido y para medir la longitud de onda en el aire, se compone de un tubo de vidrio con un émbolo movible. Una traza de polvo de corcho sirve para mostrar la distribución de nodos y antinodos cuando se excitan con una fuente de sonido intenso.', '../Img/img35.jpg', '', 'Equipo para observar las ondas de sonido y para medir la longitud de onda en el aire.', '- Determinación de ondas estacionarias de sonido', ''),
(36, 'Tubo de Resonancia', 'Equipo para la demostración de ondas de sonido estacionarias. Sirve para medir la longitud de onda en el aire, se compone de un tubo de vidrio vertical que esta conectado con una tubería a un bulbo de nivelación. Cuando se excita por una fuente de sonido (diapasón), a determinados niveles de agua del tubo se produce un ascenso del nivel de sonido llamados puntos de resonancia.', '../Img/img36.jpg', '', 'Equipo para la demostración de ondas de sonido estacionarias. Sirve para medir la longitud de onda en el aire.', '- Determinación de la velocidad de sonido en el aire', ''),
(37, 'Hidrostática', 'El aparato se compone, en primer lugar, del recipiente transparente de medición en forma de un segmento de círculo que, para los ensayos, se llena con agua. Una escala graduada en el depósito permite la lectura exacta del nivel de agua. Al tarar el depósito mediante un brazo de palanca con los correspondientes pesos puede determinarse la fuerza imperante en el punto central de presión.', '../Img/img37.jpg', '', 'El aparato se compone, en primer lugar, del recipiente transparente de medición en forma de un segmento de círculo que, para los ensayos, se llena con agua. ', '- Presión hidrostática.', ''),
(38, 'Empuje', 'Equipo para verificar el Principio de Arquímedes. Tiene un cilindro con una banda de transporte y un gancho, que se cuelga a uno de los platillos de la balanza.', '../Img/img38.jpg', '', 'Equipo para verificar el Principio de Arquímedes.', '- Demostración del Principio de Arquímedes<br>\r\n- Medida del empuje como una función de la profundidad <br>\r\n', ''),
(39, 'Prensa Hidráulica', '\r\n<html><head></head><body>Equipo para introducir el concepto de presión. Sirve para generar sobrepresión y depresión y para experimentos de compresibilidad de gases. Lleva platillos para sujetar los pesos.</body></html>', '../Img/img39.jpg', '', 'Equipo para introducir el concepto de presión. ', '- Definición de presión<br>\r\n- Prensa hidráulica<br>\r\n', ''),
(40, 'Demostración de la Ecuación de Bernoulli', 'Consta esencialmente de un tubo de Venturi de pared transparente donde se pueden medir las secciones transversales del tubo para seis puntos de medida, que sirve para la determinación de sus áreas. Un multimanómetro de seis tubos de 300 mm. de long. Miden las presiones estáticas en cada sección del tubo. El Banco Hidráulico consta de un tanque de plástico de 60 Lt., un medidor domiciliario de agua y una bomba de ½ HP', '../Img/img40.jpg', '', 'Consta esencialmente de un tubo de Venturi de pared transparente donde se pueden medir las secciones transversales del tubo para seis puntos de medida, que sirve para la determinación de sus áreas. ', '- Presión estática en distintas secciones de un Venturímetro<br>\r\n- Determinación del caudal con un tubo de Venturi<br>\r\n', ''),
(41, 'Dilatación de sólidos', 'Para medir cuantitativamente la dilatación longitudinal de sólidos, en función del material, la longitud y la temperatura. En la placa base se sujetan tubos de metal, que se calientan haciendo circular agua caliente o vapor, que se produce en un generador de vapor, incluido en el equipo. \r\nConsta además de un reloj indicador Mitutoyo (Japón) y tres tubos de diferentes materiales, L 60 cm, &#966; 8 mm. (bronce, aluminio y acero).', '../Img/img41.jpg', '', 'Para medir cuantitativamente la dilatación longitudinal de sólidos, en función del material, la longitud y la temperatura. ', '- Expansión térmica en sólidos', ''),
(42, 'Calorimetría', 'Equipo para medir el calor específico de los cuerpos', '../Img/img42.jpg', '', 'Equipo para medir el calor específico de los cuerpos', '- Calor específico de sólidos<br>\r\n<li>Calor latente de vaporización<br>\r\n<li>Calor latente de fusión<br>\r\n', ''),
