(50, 'Leyes de Kirchoff', 'Equipo para la medición de: Corriente, Voltages en resistencias en distintas conexiones', '../Img/img50.jpg', '', 'Equipo para la medición de: Corriente, Voltages en resistencias en distintas conexiones', '- Medidas de corriente y voltajes en resistencias\r\nconectadas en serie y en paralelo<br>\r\n- Divisor de voltaje<br>\r\n- Principios del puente de Wheaststone<br>\r\n', ''),
(51, 'Circuitos eléctricos', 'Amperímetro Voltímetro', '../Img/img51.jpg', '', 'Amperímetro Voltímetro', '- Características del Amperímetro<br>\r\n- Características del Voltímetro<br>', ''),
(52, 'Potencia eléctrica', 'Experimentación de la Potencia Eléctrica', '../Img/img52.jpg', '', 'Experimentación de la Potencia Eléctrica', '- Determinación de la potencia calorífica de una carga Ohmica en un circuito AC como una función del voltaje aplicado<br>\r\n- Medida del trabajo eléctrico de un calentador de inmersión, usando un medidor domiciliario de CA<br>', ''),
(53, 'Experimentos básicos de Magnetostática', 'Experimentación y medición de Líneas de campo magnético y Fundamentos básicos del electromagnetismo\r\n', '../Img/default.jpg', '', 'Experimentación y medición de Líneas de campo magnético y Fundamentos básicos del electromagnetismo', '- Líneas de campo magnético<br>\r\n- Fundamentos básicos del electromagnetismo<br>', ''),
(54, 'Fuerza en un campo eléctrico', 'Medidas de fuerza en espiras en un campo magnético', '../Img/default.jpg', '', 'Medidas de fuerza en espiras en un campo magnético', '- Medida de fuerzas en espiras conductoras en un campo magnético', ''),
